import { Transform } from 'stream';
import StreamzConstructor from './streamz';

// Helper function for creating typed Transform streams
declare function createTypedTransform<T, R>(options: {
  transform: (chunk: T, encoding: BufferEncoding, callback: (error?: Error | null, data?: R) => void) => void;
  flush?: (callback: (error?: Error | null, data?: R) => void) => void;
}): Transform;

// Export both the main constructor and the helper
declare const streamz: typeof StreamzConstructor & {
  createTypedTransform: typeof createTypedTransform;
};

export = streamz; 